class Geissweb_Euvatgrouper_Block_Adminhtml_Setup extends Mage_Adminhtml_Block_Widget_Form_Container
{
    public function __construct()
    {
        parent::__construct();
        $this->_objectId   = 'euvat_setup';
        $this->_blockGroup = 'euvatgrouper';
        $this->_controller = 'adminhtml';
        $this->_mode       = 'setup';

        $this->_removeButton('back');
        $this->_removeButton('save');

        $this->_addButton('save', array(
            'label'     => Mage::helper('adminhtml')->__('Run Setup'),
            'onclick'   => 'setupForm.submit();',
            'class'     => 'save',
        ), 1);
    }

    public function getHeaderText()
    {
        return Mage::helper('euvatgrouper')->__('EU VAT Enhanced Setup');
    }

    public function getHeaderCssClass()
    {
        return 'head-' . strtr($this->_controller, '_', '-');
    }

}