class Geissweb_Euvatgrouper_Adminhtml_ReportController extends Mage_Adminhtml_Controller_Action
{

	public function indexAction()
	{
		$this->_title($this->__('Sales'))->_title($this->__('EU VAT Report'));
		$this->loadLayout();
		$this->_setActiveMenu('report/sales');
		//$this->_addContent($this->getLayout()->createBlock('euvatgrouper/adminhtml_report_moss_filter'));
		$this->_addContent($this->getLayout()->createBlock('euvatgrouper/adminhtml_report_moss'));
		$this->renderLayout();
	}

	public function gridAction()
	{
		$this->loadLayout();
		$this->getResponse()->setBody(
			$this->getLayout()->createBlock('euvatgrouper/adminhtml_report_moss')->toHtml()
		);
	}

	public function exportCsvAction()
	{
		$fileName = 'euvat_report.csv';
		$grid = $this->getLayout()->createBlock('euvatgrouper/adminhtml_report_moss');
		$this->_prepareDownloadResponse($fileName, $grid->getCsvFile());
	}

	public function exportXmlAction()
	{
		$fileName = 'euvat_report.xml';
		$grid = $this->getLayout()->createBlock('euvatgrouper/adminhtml_report_moss');
		$this->_prepareDownloadResponse($fileName, $grid->getXmlFile($fileName));
	}

}